#!/bin/bash

# Start dev server with monitoring
# This script starts the Vite dev server and monitors it every 30 seconds

SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
LOG_FILE="/tmp/vite-dev-server.log"
PID_FILE="/tmp/vite-dev-server.pid"

# Clean up any existing processes
echo "๐งน Cleaning up any existing dev servers..."
pkill -f "github-game.*vite" 2>/dev/null
sleep 1

# Start the dev server in the background
echo "๐ Starting dev server..."
cd "$SCRIPT_DIR"
npm run dev > "$LOG_FILE" 2>&1 &
SERVER_PID=$!
echo $SERVER_PID > "$PID_FILE"

echo "๐ Server PID: $SERVER_PID"
echo "๐ Log file: $LOG_FILE"
echo ""

# Wait a moment for server to initialize
sleep 3

# Extract port from log
PORT=$(grep -oP "Local:\s+http://localhost:\K\d+" "$LOG_FILE" 2>/dev/null | head -1)

if [ -z "$PORT" ]; then
    # Try to find port from listening ports
    PORT=$(lsof -ti:5174 2>/dev/null && echo "5174" || echo "5173")
fi

echo "๐ Detected port: $PORT"
echo ""

# Run the monitoring script
"$SCRIPT_DIR/check-server.sh" "$PORT" &
MONITOR_PID=$!

# Function to cleanup on exit
cleanup() {
    echo ""
    echo "๐ Shutting down..."
    kill $MONITOR_PID 2>/dev/null
    kill $SERVER_PID 2>/dev/null
    rm -f "$PID_FILE"
    exit 0
}

trap cleanup SIGINT SIGTERM

# Show log output
echo "๐ Server output (Ctrl+C to stop):"
